mapper.select:
SELECT user_id,user_nick,user_img,user_sex       
FROM users
WHERE user_id=#{tid}

SELECT COUNT(*)
FROM tour,text 
WHERE tour.text_no=text.text_no AND text_loc=#{text_no}

SELECT tour_no,text_loc,tour_theme,text_regdate,text_total_person
FROM tour,text 
WHERE tour.text_no=text.text_no AND user_id=#{id}

SELECT count(*)
FROM reservation
WHERE tour_no=#{no}

mapper.update:
UPDATE reservation SET
reservation_check='Y'
WHERE user_id=#{user_id} AND tour_no=#{tour_no}

UPDATE reservation SET
reservation_check='N'
WHERE user_id!=#{user_id} AND tour_no=#{tour_no}
